import React, { useEffect, useState } from 'react';

const ADMIN_PASSWORD = 'letmein'; // ðŸ” Change this to anything you want
const [showPassword, setShowPassword] = useState(false);


const Admin = () => {
  const [orders, setOrders] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem('adminAuthed') === 'true'
  );
  const [passwordInput, setPasswordInput] = useState('');

  useEffect(() => {
    if (isAuthenticated) {
      fetchOrders();
    }
  }, [isAuthenticated]);

  const fetchOrders = () => {
    fetch('http://localhost:5050/api/orders')
      .then(res => res.json())
      .then(data => setOrders(data))
      .catch(err => console.error('Error fetching orders:', err));
  };

  const handleLogin = () => {
    if (passwordInput === ADMIN_PASSWORD) {
      setIsAuthenticated(true);
      localStorage.setItem('adminAuthed', 'true');
    } else {
      alert('Incorrect password!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminAuthed');
    setIsAuthenticated(false);
    setPasswordInput(''); // ðŸ‘ˆ Clears the password field
  };



  const markComplete = (id) => {
    fetch(`http://localhost:5050/api/orders/${id}/complete`, {
      method: 'PATCH',
    })
      .then(res => res.json())
      .then(() => fetchOrders())
      .catch(err => console.error('Error marking order complete:', err));
  };

  const deleteOrder = (id) => {
    fetch(`http://localhost:5050/api/orders/${id}`, {
      method: 'DELETE'
    })
      .then(res => res.json())
      .then(() => fetchOrders())
      .catch(err => console.error('Error deleting order:', err));
  };

  if (!isAuthenticated) {
    return (
        <form
        onSubmit={(e) => {
          e.preventDefault(); // prevent page reload
          handleLogin();
        }}
        style={{ padding: '2rem' }}
      >
        <h2>Admin Login</h2>
        <input
          type={showPassword ? 'text' : 'password'} // ðŸ‘ˆ toggles visibility
          placeholder="Enter password"
          value={passwordInput}
          onChange={e => setPasswordInput(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>

    );
  }

  return (

    <div style={{ padding: '2rem' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
  <h1>Order Dashboard</h1>
  <button onClick={handleLogout}>ðŸšª Logout</button>
</div>


      {orders.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ width: '100%', textAlign: 'left' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Item</th>
              <th>Qty</th>
              <th>Pickup Time</th>
              <th>Request</th>
              <th>Ordered At</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id} style={{ backgroundColor: order.completed ? '#d4edda' : 'white' }}>
                <td>{order.name}</td>
                <td>{order.phone}</td>
                <td>{order.menuItem}</td>
                <td>{order.quantity}</td>
                <td>{order.pickupTime || '-'}</td>
                <td>{order.specialRequest || '-'}</td>
                <td>{new Date(order.createdAt).toLocaleString()}</td>
                <td>
                  {!order.completed ? (
                    <>
                      <button onClick={() => markComplete(order._id)}>âœ… Mark Complete</button>{' '}
                      <button onClick={() => deleteOrder(order._id)}>ðŸ—‘ Delete</button>
                    </>
                  ) : (
                    <span>âœ… Done</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Admin;
