import React, { useEffect, useState } from 'react';

const MenuManager = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [newItem, setNewItem] = useState('');

  useEffect(() => {
    fetchMenuItems();
  }, []);

  const fetchMenuItems = () => {
    fetch('http://localhost:5050/api/menu')
      .then(res => res.json())
      .then(data => setMenuItems(data))
      .catch(err => console.error('Failed to fetch menu items:', err));
  };

  const addMenuItem = () => {
    if (!newItem.trim()) return;

    fetch('http://localhost:5050/api/menu', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: newItem.trim().toLowerCase() })
    })
      .then(res => res.json())
      .then(() => {
        setNewItem('');
        fetchMenuItems();
      })
      .catch(err => console.error('Failed to add item:', err));
  };

  const deleteMenuItem = (id) => {
    fetch(`http://localhost:5050/api/menu/${id}`, {
      method: 'DELETE'
    })
      .then(res => res.json())
      .then(() => fetchMenuItems())
      .catch(err => console.error('Failed to delete item:', err));
  };

  return (
    <div style={{ padding: '1rem', marginTop: '2rem', background: '#f9f9f9', borderRadius: '8px' }}>
      <h2>ðŸ“‹ Manage Menu</h2>
      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="New item name"
          value={newItem}
          onChange={e => setNewItem(e.target.value)}
        />
        <button onClick={addMenuItem} style={{ marginLeft: '0.5rem' }}>âž• Add</button>
      </div>
      <ul>
        {menuItems.map(item => (
          <li key={item._id}>
            {item.name}
            <button
              onClick={() => deleteMenuItem(item._id)}
              style={{ marginLeft: '1rem', color: 'red' }}
            >
              ðŸ—‘ Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MenuManager;
